export IFS=, && cat summer\ 2019.csv | grep District | while read day date rink time ampm home away; do osascript -e "tell application \"iCal\" to make new event at end of calendar 7 with properties {start date:date \"$date $(echo $time | sed -E -e 's/([0-9]{1,2})([0-9]{2})/\1:\2/g') PM\", end date:date \"$date $(echo $time | sed -E -e 's/([0-9]{1,2})([0-9]{2})/\1:\2/g'  | awk '{split($0,a,":"); print (a[1] + 1) ":"a[2]}') PM\", summary:\"$home vs $away\", description: \"Rink: $rink\"}"; done
